import React from 'react'
import {
    Card, CardText, CardBody,
    CardTitle, Button, Label, Input
  } from 'reactstrap';
import axios from 'axios'


class Cards extends React.Component {
    constructor(props) {
      super(props);
  
      this.onChange = {
        name1: this.handleChange.bind(this, 'name1'),
        name2: this.handleChange.bind(this, 'name2'),
      }
  
      this.state = {
        name1: '',
        name2: ''
      };
      
      this.getMoney = this.getMoney.bind(this);
    };
  
    handleChange(name, event) {
      this.setState({ [name]: event.target.value });
    };

    getMoney() {
        axios.get('https://free.currconv.com/api/v7/convert?apiKey=do-not-use-this-key&q=EUR_ARS,USD_ARS')
          .then(function (response) {
            let EURtoARS = response.data.results.EUR_ARS.val;
            let USDtoARS = response.data.results.USD_ARS.val;
            this.setState(state => ({
                euro: EURtoARS,
                dolar: USDtoARS
              }));
            console.log(EURtoARS+' -- '+USDtoARS);
          })
          .catch(function (error) {
            // handle error
            console.log(error);
          });
    }  

render(){
    return (
        <div>
          <Card className="col-12 mt-5 border-0">
              <div className="row mx-auto">
                  <div className="col-6">
                  <div className="input-group">
                    <div className="input-group-prepend">
                        <span className="input-group-text" id="basic-addon">
                        <i class="fa fa-usd" aria-hidden="true"></i>
                        </span> 
                    </div>
                   <input type="text" className="form-control" onChange={ (e) => this.handleChange(e, 'valor1') }  placeholder="DOLAR"/>
                </div>
                 </div>
                  <div className="col-6">
                  <div className="input-group">
                    <div className="input-group-prepend">
                        <span className="input-group-text" id="basic-addon">
                        <i class="fa fa-euro" aria-hidden="true"></i>
                        </span> 
                    </div>
                   <input type="text" className="form-control" onChange={ (e) => this.handleChange(e, 'valo2')  } placeholder="EURO"/>
                </div>                  
                  </div>
                  <div className="col12">
                    
        <Input onChange={ this.onChange.name1 } />
        <Input onChange={ this.onChange.name2 } />
                  </div>
                <div className="col-12 mt-5 text-center">
                  ARS to USD: {this.state.name1} <br/>
                  ARS to EURO: {this.state.name2} <br/>
              <Button>Convertir</Button>
                </div>
            </div>
          </Card>
        </div>
        )
}
}
export default Cards
