import React from 'react'
import {
    Card, CardText, CardBody,
    CardTitle, Button
  } from 'reactstrap';
import axios from 'axios'


class Cards extends React.Component {

    constructor(props){
        super(props)
        this.state = {
            euro: '',
            dolar: ''
        } 
        this.getMoney = this.getMoney.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }

    getMoney() {
        axios.get('https://free.currconv.com/api/v7/convert?apiKey=do-not-use-this-key&q=EUR_ARS,USD_ARS')
          .then(function (response) {
            let EURtoARS = response.data.results.EUR_ARS.val;
            let USDtoARS = response.data.results.USD_ARS.val;
            this.setState(state => ({
                euro: EURtoARS,
                dolar: USDtoARS
              }));
            console.log(EURtoARS+' -- '+USDtoARS);
          })
          .catch(function (error) {
            // handle error
            console.log(error);
          });
    }
    handleChange(e) {
      this.setState(state => ({
        cantidad: e.target.value
      }));
    }

render(){
    return (
        <div>
          <Card className="col-12 mt-5 border-0">
              <div className="row mx-auto">
                  <div className="col-6">
                  <div className="input-group">
                    <div className="input-group-prepend">
                        <span className="input-group-text" id="basic-addon">
                        <i class="fa fa-usd" aria-hidden="true"></i>
                        </span> 
                    </div>
                   <input type="text" className="form-control" value={this.state.value} onChange={(e) => {this.handleChange(e)}} placeholder="DOLAR"/>
                </div>
                 </div>
                  <div className="col-6">
                  <div className="input-group">
                    <div className="input-group-prepend">
                        <span className="input-group-text" id="basic-addon">
                        <i class="fa fa-euro" aria-hidden="true"></i>
                        </span> 
                    </div>
                   <input type="text" className="form-control" value={this.state.value} onChange={(e) => {this.handleChange(e)}} placeholder="EURO"/>
                </div>                  
                  </div>
                <div className="col-12 mt-5 text-center">
              <Button>Convertir</Button>
                </div>
            </div>
          </Card>
        </div>
        )
}
}
export default Cards
