import React from 'react';
import './styles/FeedTextBox.css'
import { Form, FormGroup, Label, Input, FormFeedback, FormText, Button, DropdownItem, Modal } from 'reactstrap';
import Entradas from '../components/Firebase/Entradas'
import Collection from './Firebase/Collection'
import InfoLogin from './InfoLogin';
import Modals from './Modals'

export default class FeedTextBox extends Collection  {
   
    constructor(props) {
        super(props);
        this.state = {
          modal: false
        };
    
        this.toggle = this.toggle.bind(this);
      }
    
      toggle() {
        this.setState({
          modal: !this.state.modal
        });
      }
    
    render() {
        
        const {inputValue} = this.state;
        

        return (
            <div>
                {localStorage.getItem('logueado') == null ?
                <div>
                    
                </div>
                :
                <div className="row justify-content-center">
                    {/* <div className="col-12 d-flex justify-content-center">
                                <div className="border-bottom-purecss-example align-items-center">
                                <h2 className="streamers-title pt-2"> Top Clips </h2>
                                </div>
                        </div> */}
                        <Form name="entradas" className="bg-light mt-2 p-2 rounded shadow text-right col-9 col-sm-9 col-md-9 col-lg-6">
                        <FormGroup>
                            <Input onFocus={this.onFocus} ref={this.theFocus} value={inputValue} onChange={this.changeValue} type="textarea" maxLength="140" className="border-light textBox" placeholder="Mensaje de hasta 140 caracteres." />
                        </FormGroup>

                       {/* 
                         */}
                        <Button className="border-light shadow p-1 col-3 textBox clip" onClick={this.toggle}>{this.props.buttonLabel} >+ Clip</Button>
                        
                        
                        <Button className="bg-light p-1 text-dark shadow border-light col-9 float-left" onClick={this.action} >Postear</Button>
                        </Form>
                        <Entradas/>

        <Modal isOpen={this.state.modal} toggle={this.toggle} className={this.props.className}>
          <ModalHeader toggle={this.toggle}>Modal title</ModalHeader>
          <ModalBody>
            Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
          </ModalBody>
          <ModalFooter>
            <Button color="primary" onClick={this.toggle}>Do Something</Button>{' '}
            <Button color="secondary" onClick={this.toggle}>Cancel</Button>
          </ModalFooter>
        </Modal>

                </div>
                }
            </div>
        );
    }
}
