import React from 'react';
import './styles/FeedTextBox.css'
import { Form, FormGroup, Label, Input, FormFeedback, FormText, Button } from 'reactstrap';
import Entradas from '../components/Firebase/Entradas'
import Collection from './Firebase/Collection'
import InfoLogin from './InfoLogin';

export default class FeedTextBox extends Collection  {
    constructor(props){
        super(props)
        
        this.emailInput = React.createRef();
        
    }
    componentDidMount() {
        this.emailInput.current.focus(function(){
            this.animate({height: "4em"},500)
        });
      } 
    render() {

        const {inputValue} = this.state;
        return (
            <div>
                {localStorage.getItem('logueado') == null ?
                <div>
                    
                </div>
                :
                <div>
                    {/* <div className="col-12 d-flex justify-content-center">
                                <div className="border-bottom-purecss-example align-items-center">
                                <h2 className="streamers-title pt-2"> Top Clips </h2>
                                </div>
                        </div> */}
                        <Form name="entradas" className="bg-light p-2 rounded shadow text-right">
                        <FormGroup>
                            <Input ref={this.emailInput} value={inputValue} onChange={this.changeValue} type="textarea" maxLength="140" className="border-light p-1 col-12 float-left textBox" placeholder="Mensaje de hasta 140 caracteres." />
                        </FormGroup>
                        <Button className="border-light shadow p-1 col-3 textBox clip" >+ Clip</Button>
                        <Button className="bg-light p-1 text-dark shadow border-light col-9 float-left" onClick={this.action} >Postear</Button>
                        
                        </Form>
                        <Entradas/>
                </div>
                }
            </div>
        );
    }
}
