import React, {Component} from 'react'
import {Modal, ModalBody, ModalFooter, ModalHeader, Button, Card} from 'reactstrap'

class CuerpoFeedBox extends React.Component{
  
  constructor(props) {
    super(props);
    this.state = {
      nestedModal: false,
    };

    this.toggleNested = this.toggleNested.bind(this);
  }


  toggleNested() {
    this.setState({
      nestedModal: !this.state.nestedModal,
      closeAll: false
    });
  }


  render(){
    const { embedClip,thumbClip,titleClip,urlClip,countClip } = this.props

    return (
      <div className="col-6">
        <Card className="text-white text-center mb-4 same-height" > 

        <div className="container-fluid">
                            <div className="row">
                                <div className="col-md-6 d-flex bg-success p-5" style={{backgroundImage: "url(" + thumbClip + ")"}}>
                                    <h1 className="mx-auto align-self-center"><i className="fa fa-2x fa-tree"></i></h1>
                                </div>
                                <div className="col-md-6 py-3 bg-dark">
                                    <h3 className="card-title">{titleClip}</h3>
                                    <p className="card-text">Views: {countClip} </p>
                                    <a href="#" className="btn btn-outline-success btn-block">Seleccionar</a>
                                    <a href="#" className="btn btn-outline-success btn-block">Descargar</a>
                                    <a className="btn btn-outline-success btn-block" onClick={this.toggleNested}>Ver clip</a>
                                </div>
                            </div>
                          
            <Modal className="modal-lg" isOpen={this.state.nestedModal} toggle={this.toggleNested} onClosed={this.state.closeAll ? this.toggle : undefined}>
              <ModalHeader>{titleClip}</ModalHeader>
              <ModalBody><iframe src={embedClip} width="100%" height="400px"></iframe> </ModalBody>
              <ModalFooter>
                <Button color="secondary" onClick={this.toggleNested}>Dejar de ver</Button>{' '}
                <Button className="clip" onClick={this.toggleAll}>Cerrar</Button>
              </ModalFooter>
            </Modal>
                        </div>

    </Card>
    </div>
    )
  }
}

export default CuerpoFeedBox
