import React, {Component} from 'react'
import {
    Button,
    Form

  //   NavbarText
  } from 'reactstrap';
  import NotesBody from '../components/NotesBody'
  import AssistsBody from '../components/AssistsBody'
import '../components/styles/inicio.css'


class Inicio extends React.Component {

    constructor(props) {
        super(props);
        this.state = {value: ''};
    
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }
    
      handleChange(event) {
        this.setState({value: event.target.value});
      }
    
      handleSubmit(event) {
        event.preventDefault();
        localStorage.setItem('username',this.state.value)
        window.reload()
      }

    render(){
        const local = localStorage.getItem('username')
    return (
        <div>
            {local ?             
                <div className="row">
                <div className="col-12 col-sm-12 col-md-3 justify-content-start">
                <NotesBody />
                </div>
                <div className="col-12 col-sm-12 col-md-9">
                <AssistsBody/>
                </div>
                </div>
                :
                <div className="row">
                    <div className="todo-center col-12 d-flex justify-content-center align-items-center">

                            <form onSubmit={this.handleSubmit} className="form mx-auto align-items-center">
                        <p className="text-center text-white">Porfavor, coloca tu nombre.</p> 
                                <div className="form-group">
                                <label className="form-label">
                                <input value={this.state.value} onChange={this.handleChange} className="form-control"/>
                                </label>
                                </div>
                                <button type="submit" value="Submit" className="btn btn-primary container-fluid">Aceptar </button>
                            </form>
                    </div>
                </div>
                }
        </div>
    )
}

}
export default Inicio
