import React from 'react';
import './styles/FeedTextBox.css'
import { Form, FormGroup, Label, Input, FormFeedback, FormText, Button, DropdownItem } from 'reactstrap';
import Entradas from '../components/Firebase/Entradas'
import Collection from './Firebase/Collection'
import InfoLogin from './InfoLogin';

export default class FeedTextBox extends Collection  {
    constructor() {
        super();
    
        this.theFocus = React.createRef();
    }
    onFocus = (e) => { 
        this.theFocus.current.focus(function(){
        });
    }
    
    
    render() {
        
        let stylesBox = {
            estilos: 'border-light textBox'
        }
        const {inputValue} = this.state;
        return (
            <div>
                {localStorage.getItem('logueado') == null ?
                <div>
                    
                </div>
                :
                <div className="row justify-content-center">
                    {/* <div className="col-12 d-flex justify-content-center">
                                <div className="border-bottom-purecss-example align-items-center">
                                <h2 className="streamers-title pt-2"> Top Clips </h2>
                                </div>
                        </div> */}
                        <Form name="entradas" className="bg-light mt-2 p-2 rounded shadow text-right col-9 col-sm-12 col-md-12 col-lg-4">
                        <FormGroup>
                            <Input onFocus={this.onFocus} ref={this.theFocus} value={inputValue} onChange={this.changeValue} type="textarea" maxLength="140" className={stylesBox.estilos} placeholder="Mensaje de hasta 140 caracteres." />
                        </FormGroup>
                        <Button className="border-light shadow p-1 col-3 textBox clip" >+ Clip</Button>
                        <Button className="bg-light p-1 text-dark shadow border-light col-9 float-left" onClick={this.action} >Postear</Button>
                        </Form>
                        <Entradas/>
                </div>
                }
            </div>
        );
    }
}
