import React, {Component} from 'react'
import db from './FirebaseConfig'
import InfoLogin from '../InfoLogin'

export default class Collection extends InfoLogin{

    state = {
        entradas:[]
    }

    changeValue = (e) => {
        this.setState({
            inputValue: e.target.value
        })
    }

    action = () =>{
        const {inputValue} = this.state;
        db.collection("entradas").add({
            post: inputValue,
            user: this.state.user,
            time: Date()
        }).then( ()=> {
            console.log('Added')
        }).catch( () => {
            console.log('error')
        });
    }

    componentDidMount(){
        db.collection('entradas').onSnapshot((snapShots) => {
            this.setState({
                entradas: snapShots.docs.map( doc => { 
                //Retornar un for que imprima todos los registros
                     return {id: doc.id, data: doc.data()}
                //console.log(doc.data());
                })
            })
        })
    }
}
