import React, {Component} from 'react'
import {Modal, ModalBody, ModalFooter, ModalHeader, Button, Card} from 'reactstrap'

class CuerpoFeedBox extends React.Component{
  
  constructor(props) {
    super(props);
    this.state = {
      nestedModal: false,
    };

    this.toggleNested = this.toggleNested.bind(this);
  }


  toggleNested() {
    this.setState({
      nestedModal: !this.state.nestedModal,
      closeAll: false
    });
  }


  render(){
    const { embedClip,thumbClip,titleClip,urlClip,countClip } = this.props

    return (
      <div className="col-4">
        <Card className="text-white text-center pt-2 mb-4" style={{backgroundImage: "url(" + thumbClip + ")"}}> 

        <div class="card-body card-title">
                                    <h1 class="m-b-2"> {titleClip} </h1>
                                    <h6 class="text-light">{countClip}</h6>
                          

  <Button href={urlClip} className="px-2 fa-lg li-ic mr-1 clip">Seleccionar</Button>
  <Button href={urlClip} className="px-2 fa-lg li-ic mr-1 secondary">Descargar</Button>

  <Button className="mt-1" color="warning" onClick={this.toggleNested}>Ver Clip</Button>
            <Modal className="modal-lg" isOpen={this.state.nestedModal} toggle={this.toggleNested} onClosed={this.state.closeAll ? this.toggle : undefined}>
              <ModalHeader>{titleClip}</ModalHeader>
              <ModalBody><iframe src={embedClip} width="100%" height="400px"></iframe> </ModalBody>
              <ModalFooter>
                <Button color="secondary" onClick={this.toggleNested}>Dejar de ver</Button>{' '}
                <Button className="clip" onClick={this.toggleAll}>Cerrar</Button>
              </ModalFooter>
            </Modal>
      </div>
    </Card>
    </div>
    )
  }
}

export default CuerpoFeedBox
