import React, {useState} from 'react';
import './styles/FeedTextBox.css'
import { Form, FormGroup, Label, Input, FormFeedback, FormText, Button, DropdownItem, Modal } from 'reactstrap';
import Entradas from '../components/Firebase/Entradas'
import Collection from './Firebase/Collection'
import InfoLogin from './InfoLogin';

export default class FeedTextBox extends Collection  {
   
    
    render() {
        
        const {inputValue} = this.state;
        function Example() {
            const [show, setShow] = useState(false);
          
            const handleClose = () => setShow(false);
            const handleShow = () => setShow(true);
          
            return (
              <>
                <Button variant="primary" onClick={handleShow}>
                  Launch demo modal
                </Button>
          
                <Modal show={show} onHide={handleClose}>
                  <Modal.Header closeButton>
                    <Modal.Title>Modal heading</Modal.Title>
                  </Modal.Header>
                  <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
                  <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                      Close
                    </Button>
                    <Button variant="primary" onClick={handleClose}>
                      Save Changes
                    </Button>
                  </Modal.Footer>
                </Modal>
              </>
            );
          }
        return (
            <div>
                {localStorage.getItem('logueado') == null ?
                <div>
                    
                </div>
                :
                <div className="row justify-content-center">
                    {/* <div className="col-12 d-flex justify-content-center">
                                <div className="border-bottom-purecss-example align-items-center">
                                <h2 className="streamers-title pt-2"> Top Clips </h2>
                                </div>
                        </div> */}
                        <Form name="entradas" className="bg-light mt-2 p-2 rounded shadow text-right col-9 col-sm-9 col-md-9 col-lg-6">
                        <FormGroup>
                            <Input onFocus={this.onFocus} ref={this.theFocus} value={inputValue} onChange={this.changeValue} type="textarea" maxLength="140" className="border-light textBox" placeholder="Mensaje de hasta 140 caracteres." />
                        </FormGroup>

                        <Example />
                        
                        <Button className="bg-light p-1 text-dark shadow border-light col-9 float-left" onClick={this.action} >Postear</Button>
                        </Form>
                        <Entradas/>
                </div>
                }
            </div>
        );
    }
}
