import React, {Component} from 'react'
import {Modal, ModalBody, ModalFooter, ModalHeader, Button} from 'reactstrap'

class CuerpoFeedBox extends React.Component{
  
  constructor(props) {
    super(props);
    this.state = {
      modal: false,
      nestedModal: false,
      closeAll: false
    };

    this.toggle = this.toggle.bind(this);
    this.toggleNested = this.toggleNested.bind(this);
    this.toggleAll = this.toggleAll.bind(this);
  }

  toggle() {
    this.setState({
      modal: !this.state.modal
    });
  }

  toggleNested() {
    this.setState({
      nestedModal: !this.state.nestedModal,
      closeAll: false
    });
  }

  toggleAll() {
    this.setState({
      nestedModal: !this.state.nestedModal,
      closeAll: true
    });
  }
  
  render(){
    const { embedClip,thumbClip,titleClip,urlClip,countClip } = this.props

    return (
      <div className="card card-cascade wider">
<div className="view view-cascade overlay">
  <img className="card-img-top" src={thumbClip} alt={titleClip} />
  <a href={embedClip}>
    <div className="mask rgba-white-slight"></div>
  </a>

<div className="card-body card-body-cascade text-center">


  <h4 clasNames="card-title"><strong> {titleClip} </strong></h4>

  <h5 className="blue-text pb-2"><strong>Reproducci√≥nes</strong></h5>

  <p className="card-text">{countClip}</p>


  <Button href={urlClip} className="px-2 fa-lg li-ic mr-1 secondary">Descargar</Button>

  <Button className="clip" onClick={this.toggleNested}>Ver Clip</Button>
            <Modal isOpen={this.state.nestedModal} toggle={this.toggleNested} onClosed={this.state.closeAll ? this.toggle : undefined}>
              <ModalHeader>{titleClip}</ModalHeader>
              <ModalBody><iframe src={embedClip} width="450px"></iframe> </ModalBody>
              <ModalFooter>
                <Button color="secondary" onClick={this.toggleNested}>Dejar de ver</Button>{' '}
                <Button className="clip" onClick={this.toggleAll}>Cerrar</Button>
              </ModalFooter>
            </Modal>
</div>

  </div>
    </div>
    )
  }
}

export default CuerpoFeedBox
