import React from 'react'
import {
    Card, CardText, CardBody,
    CardTitle, Button
  } from 'reactstrap';
import axios from 'axios'

class Cards extends React.Component {

    constructor(props){
        super(props)
        this.state = {
            euro: '',
            dolar: ''
        } 
        this.getMoney = this.getMoney.bind(this);
    }

    getMoney() {
        axios.get('https://free.currconv.com/api/v7/convert?apiKey=do-not-use-this-key&q=EUR_ARS,USD_ARS')
          .then(function (response) {
            let EURtoARS = response.data.results.EUR_ARS.val;
            let USDtoARS = response.data.results.USD_ARS.val;
            this.setState(state => ({
                euro: EURtoARS,
                dolar: USDtoARS
              }));
            console.log(EURtoARS+' -- '+USDtoARS);
          })
          .catch(function (error) {
            // handle error
            console.log(error);
          });
    }

render(){
    return (
        <div>
          <Card className="col-4 mx-auto mt-3">
              <div className="row center align-items-center">
                  <div className="col-6">
            <CardBody className="text-white">
              <CardTitle>AhoraCambio</CardTitle>
              <CardText className="text-dark">
              <div className="input-group">
      <div className="input-group-prepend">
        <span className="input-group-text" id="basic-addon">
          <i className="fa fa-user prefix"></i>
        </span>
      </div>
      <input type="text" className="form-control" placeholder="Username" aria-label="Username" aria-describedby="basic-addon" />
    </div>
              </CardText>
              <Button>Convertir</Button>
            </CardBody>
                 </div>
                  <div className="col-6">
            <CardBody className="text-white">
              <CardTitle>AhoraCambio</CardTitle>
              <CardText className="text-dark">
              <input value={this.state.value} onChange={(e) => {this.handleChange(e)}} placeholder="DOLAR"/>
              <input value={this.state.value} onChange={(e) => {this.handleChange(e)}} placeholder="ARS"/>
              </CardText>
              <Button>Convertir</Button>
            </CardBody>
             </div>
            </div>
          </Card>
        </div>
        )
}
}
export default Cards
