import React from 'react'
import NotesImg from '../images/notes.png'
import imgBack from '../images/circles.png'
import {
    Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle, Button
  } from 'reactstrap';
/* 
const user = {
    firstName: 'Juan',
    lastName: 'Gidoni'
}

function getName(user) {
    return `${user.firstName} ${user.lastName}`
}

function getGreeting(user){
    if(user){
        return <h1>Hola, {getName(user)}.</h1>
    }
    return <h1>Hola, "extraño".</h1>
}

const name = 'Juan'
const element = (
    <div>
     {getGreeting(user)} 
     </div>
)
*/
class Cards extends React.Component {
render(){
    return (
        <div>
          <Card className="col-4 mx-auto mt-3" 
          style={{
              backgroundImage: `url(${imgBack}), linear-gradient(to right, ${this.props.leftColor}, ${this.props.rightColor})`
          }}>
              <div className="row center align-items-center">
                  <div className="col-6">
            <CardImg top width="100%" src={this.props.img} alt="Card image cap" />
                 </div>
                  <div className="col-6">
            <CardBody className="text-white">
              <CardTitle>{this.props.notaTitle}</CardTitle>
              <CardText>{this.props.notaBody}</CardText>
              <Button>Editar</Button>
            </CardBody>
             </div>
            </div>
          </Card>
        </div>
        )
}
}
export default Cards
