import React from 'react';
import './styles/FeedTextBox.css'
import { Form, FormGroup, Label, Input, FormFeedback, FormText, Button } from 'reactstrap';

const FeedTextBox = (props) => {
  return (
      <div>
          <div className="col-12 d-flex justify-content-center">
                    <div className="border-bottom-purecss-example align-items-center">
                    <h2 className="streamers-title pt-2"> Top Clips </h2>
                    </div>
             </div>
             <Form className="bg-light p-2 rounded shadow text-right">
            <FormGroup>
                <Input type="textarea" maxlength="140" className="border-light p-1 col-12 float-left textBox" placeholder="Mensaje de hasta 140 caracteres." />
            </FormGroup>
            <Button className="border-light shadow p-1 col-3 textBox clip" >+ Clip</Button>
            <Button className="bg-light p-1 text-dark shadow border-light col-9 float-left">Postear</Button>
            </Form>
      </div>
  );
}

export default FeedTextBox;
